mapper.select:
SELECT SUD.User_id As userId ,SDI.Db AS db ,SDI.Db_ver AS dbVer ,SUM(SDI.Db_period) AS dbPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyDBInfo SDI ON SUD.Career_id = SDI.Career_id WHERE SUD.Delete_flg = '0'
AND SDI.Db = #{db}
AND SDI.Db_ver LIKE CONCAT('%',#{dbVer},'%')
GROUP BY userid
HAVING dbPeriod >= #{dbPeriod}
HAVING #{dbPeriod} >= dbPeriod

SELECT SUD.User_id As userId ,SFI.Fw_nw AS fwNw ,SFI.Fw_nw_ver AS fwNwVer ,SUM(SFI.Fw_nw_period) AS fwNwPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyFwNwInfo SFI ON SUD.Career_id = SFI.Career_id WHERE SUD.Delete_flg = '0'
AND SFI.Fw_nw = #{fwNw}
AND SFI.Fw_nw_ver LIKE CONCAT('%',#{fwNwVer},'%')
GROUP BY user_id
HAVING fwNwPeriod >= #{fwNwPeriod}
HAVING fwNwPeriod >= fwNwPeriod

SELECT SUD.User_id As userId ,SOI.Os AS os ,SOI.Os_ver AS osVer ,SUM(SOI.Os_period) AS osPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyOSInfo SOI ON SUD.Career_id = SOI.Career_id WHERE SUD.Delete_flg = '0'
AND SOI.Os = #{os}
AND SOI.Os_ver LIKE CONCAT('%',#{osVer},'%')
GROUP BY userid
HAVING osPeriod >= #{osPeriod}
HAVING #{osPeriod} >= osPeriod

SELECT SUD.User_id As userId ,SLI.Language AS language ,SLI.Language_ver AS languageVer ,SUM(SLI.Language_period) AS languagePeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyLanguageInfo SLI ON SUD.Career_id = SLI.Career_id WHERE SUD.Delete_flg = '0'
AND SLI.Language = #{language}
AND SLI.Language_ver LIKE CONCAT('%',#{languageVer},'%')
GROUP BY userid
HAVING languagePeriod >= #{languagePeriod}
HAVING #{languagePeriod} >= languagePeriod

SELECT SUD.User_id AS userId ,SOI.Other AS other ,SOI.Other_ver AS otherVer ,SUM(SOI.Other_period) AS otherPeriod FROM StarrilyUserDetailInfo SUD JOIN StarrilyOtherInfo SOI ON SUD.Career_id = SOI.Career_id WHERE SUD.Delete_flg = '0'
AND SOI.Other = #{other}
AND SOI.Other_ver LIKE CONCAT('%',#{otherVer},'%')
GROUP BY userid
HAVING otherPeriod >= #{otherPeriod}
HAVING #{otherPeriod} >= otherPeriod

mapper.update:
UPDATE
    StarrilyUserDetailInfo
SET
    Delete_flg = '1',
    Update_date = CURRENT_TIMESTAMP()
WHERE
    Career_id = #{careerId}
    AND User_id = #{userId}
    AND Update_date  <=  #{updateDate}

UPDATE
	starrilyrole
SET
	user_role = #{authority}
WHERE
	user_id = #{userId}
